for lop:
#include <iostream>
using namespace std;

int main() {
    for (int i = 1; i <= 5; i++) {
        cout << "For Loop Iteration: " << i << endl;
    }
    return 0;
}

while lop:
#include <iostream>
using namespace std;

int main() {
    int count = 1;
    while (count <= 5) {
        cout << "While Loop Iteration: " << count << endl;
        count++;
    }
    return 0;
}

do while lop:
#include <iostream>
using namespace std;

int main() {
    int count = 1;
    do {
        cout << "Do-While Loop Iteration: " << count << endl;
        count++;
    } while (count <= 5);
    return 0;
}

if else:
#include <iostream>
using namespace std;

int main() {
    int num = 10;
    if (num > 0) {
        cout << "The number is positive." << endl;
    } else {
        cout << "The number is non-positive." << endl;
    }
    return 0;
}

switch case:
#include <iostream>
using namespace std;

int main() {
    int day = 3;
    switch (day) {
        case 1:
            cout << "Monday" << endl;
            break;
        case 2:
            cout << "Tuesday" << endl;
            break;
        case 3:
            cout << "Wednesday" << endl;
            break;
        default:
            cout << "Invalid day" << endl;
    }
    return 0;
}

